import styles from "./SignUpModal.module.scss";
import { zipList } from "../../utils/ziplist";
import validator from "../../utils/passwordValidator";
import axios from "axios";

class SignUpModal extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			firstName: "",
			lastName: "",
			email: "",
			zipcode: "",
			password: "",
			profileType: "",
			errors: {
				email: "",
				zipcode: "",
				password: "",
			},
		};
	}

	handleSubmit = (event) => {
		event.preventDefault();
		const { id, value } = event.target;
		let errors = this.state.errors;

		switch (id) {
			case "email":
				errors.email = !RegExp.test(/^[^\s@]+@[^\s@]+\.[^\s@]+$/, value)
					? "Please enter a valid email"
					: "";
				break;
			case "password":
				errors.password = !validator(this.state.password)
					? console.log("pass")
					: console.log("fail");
		}
	};

	handleChange = (e) => {
		this.setState({
			[e.target.id]: e.target.value,
		});
	};
	handleButtonClick = (e) => {
		this.setState(
			{
				profileType: e.target.name,
			},
			() => console.log(this.state)
		);
	};

	render() {
		return (
			<div>
				<div className={styles.modal}>
					<h1>Sign up</h1>
					<form className={styles.SignUpModal} onSubmit={this.handleSubmit}>
						<div className={styles.fName}>
							<label htmlFor='firstName'>First name</label>
							<input
								type='text'
								id='firstName'
								value={this.state.firstName}
								onChange={this.handleChange}
								required
							></input>
						</div>
						<div className={styles.lName}>
							<label for='lastName'>Last name</label>
							<input
								type='text'
								id='lastName'
								value={this.state.lastName}
								onChange={this.handleChange}
								required
							></input>
						</div>
						<div className={styles.email}>
							<label for='email'>Email</label>
							<input
								type='text'
								id='email'
								value={this.state.email}
								onChange={this.handleChange}
								required
							></input>
						</div>
						<div className={styles.zip}>
							<label for='zipcode'>Zip code</label>
							<input
								type='text'
								id='zipcode'
								value={this.state.zipcode}
								onChange={this.handleChange}
								required
							></input>
						</div>
						<div className={styles.password}>
							<label for='password'>Password</label>
							<input
								type='password'
								id='password'
								value={this.state.password}
								onChange={this.handleChange}
								required
							></input>
						</div>
						<div className={styles.confirmPassword}>
							<label for='confirmPassword'>Confirm Password</label>
							<input type='password' id='confirmPassword' required></input>
						</div>
						<div className={styles.profileType}>
							<label for='profileTypeContainer'>Profile Type</label>
							<div className={styles.buttonContainer} id='profileTypeContainer'>
								<button
									type='button'
									name='Ind_prof'
									onClick={this.handleButtonClick}
									className={styles.profileBtn}
								>
									Indivudual/Professional
								</button>
								<button
									type='button'
									name='Band'
									onClick={this.handleButtonClick}
									className={styles.profileBtn}
								>
									Band
								</button>
								<button
									type='button'
									name='Org_biz'
									onClick={this.handleButtonClick}
									className={styles.profileBtn}
								>
									Organization/Business
								</button>
							</div>
						</div>

						<div className={styles.switch}>
							<p>Already have an account?</p>
							<a
								tabIndex='0'
								className={styles.bold}
								onClick={this.props.switchHandle}
							>
								Sign in.
							</a>
						</div>
						<button type='submit' className={styles.submit}>
							Sign in
						</button>
					</form>
				</div>
			</div>
		);
	}
}

export default SignUpModal;
