import * as passwordValidator from "password-validator";
export default function validator(state) {
	//password field validator
	///////////
	let passwordSchema = new passwordValidator();

	passwordSchema
		.is()
		.min(8) // Minimum length 8
		.is()
		.max(50) // Maximum length 50
		.has()
		.uppercase(1) // Must have uppercase letters
		.has()
		.lowercase(1) // Must have lowercase letters
		.has()
		.digits(1) // Must have at least 1 digits
		.has()
		.symbols(1) // Must have at least 1 symbol
		.has()
		.not()
		.spaces();

	let formIsValid = true;

	if ((!passwordSchema.validate(state.inputs.password), { list: true })) {
		let list = list;
		state.errors.password = `Please enter a valid password. ${list}`;
		formIsValid = false;
	}
	this.setState({ erros: state.errors });
}
