import mongoose from "mongoose";
import resourceSchema from "../models/resources";
import userSchema from "../models/users";

const dbConnect = async () => {
	const connection = await mongoose.createConnection(process.env.MONGODB_URI, {
		useNewUrlParser: true,
		bufferCommands: false,
		bufferMaxEntries: 0,
		useUnifiedTopology: true,
	});

	const Users = connection.model("Users", userSchema);
	const Resource = connection.model("Resource", resourceSchema);
	return {
		connection,
		models: {
			Users,
			Resource,
		},
	};
};

export default dbConnect;
