//import styles from "./Button.module.scss";
const Button = (props) => {
	const handleClick = (e) => {
		const style = e.target.className;
		style == "unClicked" ? (style = "clicked") : (style = "unClicked");
	};
	return (
		<div>
			<button className='unClicked' onClick={handleClick}>
				{props.children}
			</button>

			<style jsx>{`
				button {
					cursor: pointer;
					padding: 3px 8px;
					border-radius: 5px;
					border: none;
					font-family: Lato;
					font-size: 13px;
					font-weight: bold;
					text-align: center;
					color: white;
				}
				.unClicked {
					background-color: #9b9b9b;
				}
				.clicked {
					background-color: #f55701;
				}
			`}</style>
		</div>
	);
};

export default Button;
